package com.example.time.leetcode;

import java.util.HashMap;
import java.util.Map;

/*
 *@description:
给定两个字符串 s 和 t，它们只包含小写字母。
字符串 t 由字符串 s 随机重排，然后在随机位置添加一个字母。
请找出在 t 中被添加的字母。
 *@author jiafeng
 *@date 2019/12/12 0012 14:04
 */
public class FindTheDifference {
    public static void main(String[] args) {
        System.out.println(findTheDifference("",
                ""));
    }

    public static char findTheDifference(String s, String t) {
        char diff = 0;
        String[] s1 = s.split("");
        String[] t1 = t.split("");
        Map<String,Integer> map1 = new HashMap<>();
        Map<String,Integer> map2 = new HashMap<>();
        for (String s2 :s1){
            if (map1.containsKey(s2)){
                map1.put(s2,map1.get(s2)+1);
            }else {
                map1.put(s2,1);
            }
        }
        for (String t2 :t1){
            if (map2.containsKey(t2)){
                map2.put(t2,map2.get(t2)+1);
            }else {
                map2.put(t2,1);
            }
        }
        for (String t2:t1){
            if (map1.containsKey(t2)&&(map1.get(t2)==map2.get(t2))){
                continue;
            }else {
                diff = t2.charAt(0);
            }
        }
        return diff;
    }
}
